<?php// Start the sessionsession_start();if (isset($_GET['log'])) {	//file name	$file = 'php/members.txt';	//reads from existing file	$changeArray = unserialize(file_get_contents($file));						//save session data	$changeArray[$_SESSION['index']][7] += $_SESSION['scoreAdd'];	$changeArray[$_SESSION['index']][8] += $_SESSION['scoreSub'];	$changeArray[$_SESSION['index']][9] += $_SESSION['scoreMul'];	$changeArray[$_SESSION['index']][10] += $_SESSION['scoreMix'];	$changeArray[$_SESSION['index']][11] += $_SESSION['scoreTotal'];						$changeArray[$_SESSION['index']][12] += $_SESSION['totalAdd'];	$changeArray[$_SESSION['index']][13] += $_SESSION['totalSub'];	$changeArray[$_SESSION['index']][14] += $_SESSION['totalMul'];	$changeArray[$_SESSION['index']][15] += $_SESSION['totalMix'];	$changeArray[$_SESSION['index']][16] += $_SESSION['totalOp'];						file_put_contents($file, serialize($changeArray));		//clearing each session variable and destroying the session	    unset($_SESSION['loginName']);	unset($_SESSION['index']);		unset($_SESSION['scoreAdd']);	unset($_SESSION['scoreSub']);	unset($_SESSION['scoreMul']);	unset($_SESSION['scoreMix']);	unset($_SESSION['scoreTotal']);		unset($_SESSION['totalAdd']);	unset($_SESSION['totalSub']);	unset($_SESSION['totalMul']);	unset($_SESSION['totalMix']);	unset($_SESSION['totalOp']);    session_destroy();}?>